func:
const state = flow.get("rfid_profile_state") || {
  chip_uids: { "1": "", "2": "" },
  last_uid: "",
  active_profile: 0,
  hw_status: "unknown",
  train_target: 0,
  status_text: "Profile initialisiert."
};

if (!state.chip_uids || typeof state.chip_uids !== "object") {
  state.chip_uids = { "1": "", "2": "" };
}
if (typeof state.chip_uids["1"] !== "string") state.chip_uids["1"] = "";
if (typeof state.chip_uids["2"] !== "string") state.chip_uids["2"] = "";
if (!Number.isFinite(Number(state.active_profile))) state.active_profile = 0;
if (typeof state.hw_status !== "string" || state.hw_status.length === 0) state.hw_status = "unknown";
if (typeof state.status_text !== "string" || state.status_text.length === 0) state.status_text = "Profile bereit.";
if (!Number.isFinite(Number(state.train_target))) {
  state.train_target = state.train_mode ? 1 : 0;
}
state.train_target = Number(state.train_target) === 2 ? 2 : (Number(state.train_target) === 1 ? 1 : 0);

const mapHwStatusText = (hw) => {
  if (hw === "ok") return "ok";
  if (!hw) return "stoerung (unknown)";
  return "stoerung (" + hw + ")";
};

const buildLearningText = () => {
  if (state.train_target === 1) return "Anlernen aktiv: Profil 1";
  if (state.train_target === 2) return "Anlernen aktiv: Profil 2";
  return "Bereit";
};

const isLearnMessage = (text) => {
  return String(text || "").startsWith("Profil 1 angelernt:") || String(text || "").startsWith("Profil 2 angelernt:");
};

const buildUiMessage = () => ({
  payload: {
    profile_status_text: state.status_text,
    rfid_hw_status_text: mapHwStatusText(state.hw_status),
    rfid_learning_text: buildLearningText(),
    rfid_chip1_uid: state.chip_uids["1"] || "-",
    rfid_chip1_profile: "Profil 1",
    rfid_chip2_uid: state.chip_uids["2"] || "-",
    rfid_chip2_profile: "Profil 2",
    rfid_last_uid: state.last_uid || "-",
    rfid_active_profile: state.active_profile ? ("Profil " + state.active_profile) : "-"
  }
});

const saveState = () => {
  flow.set("rfid_profile_state", state);
};

const requestRfidRead = () => [{ payload: "RFID" }, buildUiMessage()];

const payloadStr = (msg.payload === undefined || msg.payload === null) ? "" : String(msg.payload);

if (payloadStr === "INIT_PROFILES") {
  state.train_target = 0;
  state.active_profile = 0;
  state.status_text = "Profile bereit.";
  saveState();
  return [null, buildUiMessage()];
}

if (payloadStr === "RFID_READ") {
  state.train_target = 0;
  state.status_text = "RFID Lesen gestartet. Chip auflegen.";
  saveState();
  return requestRfidRead();
}

if (payloadStr === "RFID_LEARN_P1") {
  state.train_target = 1;
  state.status_text = "Anlernen Profil 1 gestartet. Chip auflegen.";
  saveState();
  return requestRfidRead();
}

if (payloadStr === "RFID_LEARN_P2") {
  state.train_target = 2;
  state.status_text = "Anlernen Profil 2 gestartet. Chip auflegen.";
  saveState();
  return requestRfidRead();
}

if (payloadStr === "RFID_POLL") {
  saveState();
  return requestRfidRead();
}

const p = msg.payload || {};
if (String(p.type || "") !== "rfid") {
  return null;
}

const hwStatus = String(p.rfid_hw_status || "missing");
state.hw_status = hwStatus;

if (hwStatus !== "ok") {
  state.active_profile = 0;
  state.status_text = "RFID Modulstatus: " + hwStatus;
  saveState();
  return [null, buildUiMessage()];
}

const status = String(p.rfid_status || "missing");
const uid = String(p.rfid_uid || "").trim();

if (status === "ok" && uid) {
  state.last_uid = uid;

  if (state.train_target === 1) {
    state.chip_uids["1"] = uid;
    state.status_text = "Profil 1 angelernt: " + uid;
    state.train_target = 0;
  } else if (state.train_target === 2) {
    state.chip_uids["2"] = uid;
    state.status_text = "Profil 2 angelernt: " + uid;
    state.train_target = 0;
  }

  if (uid === state.chip_uids["1"] && state.chip_uids["1"]) {
    state.active_profile = 1;
    if (!isLearnMessage(state.status_text)) {
      state.status_text = "RFID erkannt: Profil 1 aktiv.";
    }
  } else if (uid === state.chip_uids["2"] && state.chip_uids["2"]) {
    state.active_profile = 2;
    if (!isLearnMessage(state.status_text)) {
      state.status_text = "RFID erkannt: Profil 2 aktiv.";
    }
  } else {
    state.active_profile = 0;
    if (state.train_target === 0) {
      state.status_text = "Unbekannter RFID Chip: " + uid;
    }
  }
} else if (status === "no_card") {
  state.active_profile = 0;
  if (state.train_target === 1) {
    state.status_text = "Warte auf Chip fuer Profil 1...";
  } else if (state.train_target === 2) {
    state.status_text = "Warte auf Chip fuer Profil 2...";
  } else {
    state.status_text = "Kein RFID Chip erkannt.";
  }
} else {
  state.active_profile = 0;
  if (state.train_target === 1) {
    state.status_text = "Anlernen Profil 1 fehlgeschlagen: " + status;
  } else if (state.train_target === 2) {
    state.status_text = "Anlernen Profil 2 fehlgeschlagen: " + status;
  } else {
    state.status_text = "RFID Fehler: " + status;
  }
}

saveState();
return [null, buildUiMessage()];
